mapper.insert:
insert into bus(
	bus_no, bus_num, bus_seat, bus_status, driver_no, 
	bus_model, grade_type, seat_no
)
values(
	#{busNo}, #{busNum}, #{busSeat}, #{busStatus}, #{driverNo}, 
	#{busModel}, #{gradeType}, #{seatNo}
)

mapper.update:
update bus
bus_num = #{busNum}, bus_seat = #{busSeat}, bus_status = #{busStatus}, driver_no = #{driverNo}, bus_model = #{busModel}, grade_type = #{gradeType}, seat_no = #{seatNo}
where bus_no = #{busNo}
