mapper.insert:
insert into route(
route_no, route_time, route_km,
route_start_time, route_end_time, route_start, route_end,
bus_no, route_way
)
values(
#{routeNo}, #{routeTime}, #{routeKm},
#{routeStartTime}, #{routeEndTime}, #{routeStart}, #{routeEnd},
#{busNo}, #{routeWay}
)

mapper.update:
update route
route_time = #{routeTime}, route_km = #{routeKm}, route_start_time = #{routeStartTime}, route_end_time = #{routeEndTime}, route_start = #{routeStart}, route_end = #{routeEnd}, bus_no = #{busNo}, route_way = #{routeWay}
where route_no = #{routeNo}
