mapper.insert:
insert into seat(
seat_no , seat_row , seat_column, bus_no 
)values(
#{seatNo}, #{seatRow}, #{seatColumn}, #{busNo} 
)

mapper.update:
update seat set
seat_row =#{seatRow},
seat_column =#{seatColumn},
bus_no =#{busNo}
where seat_no =#{seatNo}

update seat
seat_row=#{seatRow}, seat_column=#{seatColumn}, bus_no=#{busNo},
where seat_no = #{seatNo}
