mapper.insert:
insert into terminal(
	terminal_id, terminal_name, terminal_region
)
values(
	#{terminalId}, #{terminalName}, #{terminalRegion}
)

mapper.update:
update terminal set
	terminal_name = #{terminalName},
	terminal_region = #{terminalRegion}
where terminal_id = #{terminalId}

update terminal

	terminal_name = #{terminalName},
	terminal_region = #{terminalRegion}

where terminal_id = #{terminalId}
